import React from "react";
import { connect } from "react-redux";
import { getCategories, getActiveCategoryId } from "../selectors/Phones";
import { Link, withRouter } from "react-router";
import { compose } from "redux";
import classNames from "classnames";
import R from "ramda";

const Categories = (props) => {
  const { categories, activeCategoryId } = props;
  // console.log(" activeCategoryId " , activeCategoryId);
  let activeIdNumber = activeCategoryId - 1;
  const renderCategory = (category, index) => {
    const getActiveState = R.propEq("id", activeCategoryId);
    const linkClass = classNames({
      "list-group-item": true,
      active: getActiveState(category),
    });
    return (
      <Link to={`/categories/${category.id}`} className={linkClass} key={index}>
        {category.name}
      </Link>
    );
  };

  const renderAllCategory = () => {
    const linkClass = classNames({
      "list-group-item": true,
      active: R.isNil(activeCategoryId),
    });

    return (
      <Link to="/" className={linkClass}>
        All
      </Link>
    );
  };

  return (
    <div className="well">
      <span className="lead">Brand Filter</span>
      <div className="list-group">
        {renderAllCategory()}
        {categories.map((category, index) => {
          //console.log(category);
          return renderCategory(category, index);
        })}
      </div>
    </div>
  );
};

const mapStateToProps = (state, ownProps) => ({
  categories: getCategories(state),
  activeCategoryId: getActiveCategoryId(ownProps),
});

export default compose(withRouter, connect(mapStateToProps))(Categories);
// export default connect(mapStateToProps)(Categories);
